import React, { useState } from 'react'

const Form = () => {
    let [state,setState]=useState("")
    let [state1,setState1]=useState("")
    let [fname,setFname]=useState({fname:""})
    let [lname,setLname]=useState({lname:""})

    let getfname=(event)=>{
        setState(event.target.value)
        console.log(state)
    }
    let getlame=(event)=>{
        setState1(event.target.value)
        console.log(state1)
    }
    const handleChange = (e) => {
      if (e.target.name === 'fname') {
        setFname({ ...fname, [e.target.name]: e.target.value });
      } else if (e.target.name === 'lname') {
        setLname({ ...lname, [e.target.name]: e.target.value });
      }
    };
  
    let handleInput=(e)=>{
      //let value= e.target.value
      //let name=e.target.name
      let {value , name }=e.target;
      setState((preVal)=>{
          // if (name==="username"){
          //     return{user:value ,pass:preVal.pass}
          // }else if (name==="password"){
          //     return{user:preVal.user ,pass:value}
          // }
          switch (name){
              case "username":return{user:value ,pass:preVal.pass}
              case "password":return{user:preVal.user ,pass:value}
              default: return state
          }
      })
  }
  let handleSubmit =(e)=>{
      setState1({usernew:state.user,passnew:state.pass})
      setState({user:"" ,pass:""})
      e.preventDefault()
    }
  
  return (
    <div>
        <h1>welcome user : {state} {state1}</h1>
        <form action="">
            <input value={state} type="text" onChange={getfname}  /> <br />
            <input value={state1} type="text" onChange={getlame} /> <br />
            <button type='submit'>submit</button>
        </form>
        <div>
        <h1>username is :{state1.usernew}</h1>
        <h1>password is :{state1.passnew}</h1>
        <form action="" className="w-50 m-2" onSubmit={handleSubmit}>
            <div className="mt-2">
                <input 
                value={state.user} 
                onChange={handleInput} 
                className="form-control" 
                type="text" 
                name="username" 
                placeholder="enter your name"
                 />
            </div>
            <div className="mt-2">
                <input 
                value={state.pass} 
                onChange={handleInput} 
                className="form-control" 
                type="password" 
                name="password" 
                placeholder="enter your password" 
                />
            </div>
            <div className="mt-2">
                <button className="btn btn-outline-success" type="submit">submit</button>
            </div>
        </form>
        </div>
     
    </div>
    
  )
}

export default Form
